import * as React from "react";
import WelcomeContent from "./WelcomeContent.js";
import AuthContent from "./AuthContent.js";
import LoginForm from "./LoginForm.js";
import {request} from "../axios_helper.js";
export default class AppContent extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            componentToShow: "welcome"
        };
    };

    login = () => {
        this.setState({componentToShow: "login"});
    };

    logout = () => {
        this.setState({componentToShow: "welcome"});
    };

    onLogin = (e, username, password) => {
        e.preventDefault();
        request("POST",
            "/login",
            {login: username, password: password}
            ).then((response) => {
                this.setState({componentToShow: "messages"})
        }).catch((error) => {
            this.setState({componentToShow: "welcome"})
        });
    };

    onRegister = (e, firstName, lastName, username, password) => {
        e.preventDefault();
        request("POST",
            "/register",
            {
                firstName: firstName,
                lastName: lastName,
                login: username,
                password: password
            }
        ).then((response) => {
            this.setState({componentToShow: "messages"})
        }).catch((error) => {
            this.setState({componentToShow: "welcome"})
        });
    };

    render() {
        return(
            <div>
                {this.state.componentToShow === "welcome" && <WelcomeContent/>}
                {this.state.componentToShow === "welcome" && <AuthContent/>}
                {this.state.componentToShow === "welcome" && <LoginForm/>}
            </div>
        );
    };
}
